UPDATE author
SET updated_at = current_timestamp,
description = '<p>Engenheiro de formação e atleta de coração, nado desde que me conheço por gente. Aprecio e admiro qualquer tipo de esporte, apesar de reconhecer que nem todos são para mim. Acredito que existe algo intrínseco a todo atleta: seja profissional ou amador, todos temos um prazer pessoal em continuar experimentando, treinando, melhorando... Afinal, só uma coisa ocupa nossas mentes durante horas incessantes de treino: a próxima meta a ser batida!</p>'
WHERE id = 1;
